import React, {useState} from 'react';
import axios from 'axios';

export default function ContactForm(){
  const [form, setForm] = useState({name:'', email:'', message:''});
  const [status, setStatus] = useState(null);

  const handleChange = e => setForm({...form, [e.target.name]: e.target.value});

  const handleSubmit = async e => {
    e.preventDefault();
    try{
      await axios.post('/api/contact', form);
      setStatus('Message sent — thank you!');
      setForm({name:'', email:'', message:''});
    } catch(err){
      setStatus('Failed to send message.');
    }
  }

  return (
    <section className="my-8">
      <h2 className="text-2xl font-semibold mb-3">Contact</h2>
      <div className="card">
        <form onSubmit={handleSubmit} className="flex flex-col gap-3">
          <input name="name" value={form.name} onChange={handleChange} placeholder="Your name" className="p-2 border rounded" required />
          <input name="email" value={form.email} onChange={handleChange} placeholder="Your email" className="p-2 border rounded" required />
          <textarea name="message" value={form.message} onChange={handleChange} placeholder="Message" className="p-2 border rounded" rows="4" required />
          <button type="submit" className="px-4 py-2 rounded bg-indigo-600 text-white">Send</button>
          {status && <p className="text-sm mt-2">{status}</p>}
        </form>
      </div>
    </section>
  );
}
