import React from "react";
import { motion } from "framer-motion";

export default function Hero() {
  return (
    <section
      id="home"
      className="min-h-screen flex items-center justify-center px-6 py-20"
    >
      {/* Glassmorphism Card */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
        className="backdrop-blur-xl bg-white/40 border border-white/30 shadow-soft-lg 
                   rounded-3xl p-10 max-w-3xl mx-auto text-center"
      >
        {/* Name */}
        <h1 className="text-4xl md:text-5xl font-extrabold text-lavender-400">
          Gudipudi Durga Swathi
        </h1>

        {/* Subtitle */}
        <p className="mt-3 text-neutral-700 text-lg">
          Final-year CSE Student • MERN Stack Developer • AI Enthusiast
        </p>

        {/* Small Description */}
        <p className="mt-4 text-neutral-700 leading-relaxed max-w-xl mx-auto text-sm">
          I create elegant full-stack applications with a focus on clean UI,
          modern functionality, and smooth user experiences. Passionate about MERN,
          Python, and problem-solving.
        </p>

        {/* Buttons */}
        <div className="mt-6 flex justify-center gap-3 flex-wrap">
          <a
            href="#projects"
            className="px-5 py-2 rounded-full bg-peach-300 text-white shadow hover:shadow-md transition"
          >
            View My Projects
          </a>

          <a
            href="#contact"
            className="px-5 py-2 rounded-full border border-lavender-300 bg-white/70 text-lavender-400 hover:shadow-md transition"
          >
            Contact Me
          </a>
        </div>
      </motion.div>
    </section>
  );
}
