import React from "react";
import { motion } from "framer-motion";

export default function Skills() {
  const technicalSkills = [
    { name: "Python", level: "85%" },
    { name: "Java", level: "75%" },
    { name: "HTML & CSS", level: "90%" },
    { name: "MERN Stack", level: "80%" },
    { name: "MongoDB", level: "75%" },
    { name: "SQL / DBMS", level: "85%" },
    { name: "C Language", level: "70%" },
    { name: "Microsoft Office", level: "95%" },
  ];

  const softSkills = [
    "Teamwork",
    "Adaptability",
    "Decision Making",
    "Critical Thinking",
    "Empathy",
    "Communication",
    "Problem Solving",
    "Leadership",
  ];

  return (
    <section id="skills" className="px-8 py-20 min-h-screen">
      <h2 className="text-4xl font-extrabold text-lavender-400 text-center">
        Skills
      </h2>
      <div className="w-24 h-2 bg-peach-300 rounded-full mx-auto mt-2 mb-12"></div>

      {/* Two-column main layout */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-16 max-w-6xl mx-auto">

        {/* LEFT SIDE — TECHNICAL SKILLS */}
        <div>
          <h3 className="text-2xl font-semibold text-neutral-800 mb-6">
            Technical Skills
          </h3>

          <div className="space-y-8">
            {technicalSkills.map((skill, index) => (
              <motion.div
                key={index}
                initial={{ opacity: 0, x: -30 }}
                whileInView={{ opacity: 1, x: 0 }}
                viewport={{ once: true }}
                transition={{ duration: 0.6 }}
              >
                <div className="flex justify-between mb-2">
                  <span className="text-neutral-800 font-semibold">
                    {skill.name}
                  </span>
                  <span className="text-peach-400 font-semibold">
                    {skill.level}
                  </span>
                </div>

                <div className="w-full h-3 bg-lavender-200 rounded-full overflow-hidden">
                  <motion.div
                    initial={{ width: 0 }}
                    whileInView={{ width: skill.level }}
                    transition={{ duration: 1.2 }}
                    className="h-3 rounded-full"
                    style={{ backgroundColor: "#C8A2C8" }}
                  ></motion.div>
                </div>
              </motion.div>
            ))}
          </div>
        </div>

        {/* RIGHT SIDE — SOFT SKILLS SIDE BY SIDE */}
        <div>
          <h3 className="text-2xl font-semibold text-neutral-800 mb-6">
            Soft Skills
          </h3>

          {/* 2-column list */}
          <div className="grid grid-cols-1 sm:grid-cols-2 gap-y-4 gap-x-6 text-neutral-700 text-lg">
            {softSkills.map((s, i) => (
              <motion.div
                key={i}
                initial={{ opacity: 0, x: 20 }}
                whileInView={{ opacity: 1, x: 0 }}
                viewport={{ once: true }}
                transition={{ duration: 0.4 }}
                className="pl-3 border-l-4 border-peach-300"
              >
                {s}
              </motion.div>
            ))}
          </div>
        </div>

      </div>
    </section>
  );
}
