import React, { useState } from "react";
import { motion } from "framer-motion";

export default function Contact() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");

  const [success, setSuccess] = useState(false);

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault(); // stops page reload

    // Show success message
    setSuccess(true);

    // Clear input fields
    setName("");
    setEmail("");
    setMessage("");

    // Hide message after 3 seconds
    setTimeout(() => {
      setSuccess(false);
    }, 3000);
  };

  return (
    <section id="contact" className="px-8 pt-10 pb-20 min-h-screen">
      {/* Heading */}
      <h2 className="text-4xl font-extrabold text-lavender-400 text-center">
        Contact Me
      </h2>
      <div className="w-24 h-2 bg-peach-300 rounded-full mx-auto mt-2 mb-6"></div>

      {/* Success message */}
      {success && (
        <motion.div
          initial={{ opacity: 0, y: -10 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0 }}
          className="bg-peach-300 text-white font-semibold max-w-md mx-auto mb-6 p-4 rounded-xl text-center shadow-lg"
        >
          Message sent successfully! ðŸ’œ
        </motion.div>
      )}

      {/* Contact Card */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.7 }}
        viewport={{ once: true }}
        className="max-w-3xl mx-auto bg-white rounded-3xl shadow-soft-lg p-10 border-l-8 border-peach-300"
      >
        <form className="grid grid-cols-1 gap-6" onSubmit={handleSubmit}>

          {/* Name */}
          <div>
            <label className="block text-neutral-700 font-semibold mb-2">
              Your Name
            </label>
            <input
              type="text"
              className="w-full p-3 rounded-xl border border-neutral-300 focus:ring-2 focus:ring-lavender-300 outline-none"
              placeholder="Enter your name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </div>

          {/* Email */}
          <div>
            <label className="block text-neutral-700 font-semibold mb-2">
              Email Address
            </label>
            <input
              type="email"
              className="w-full p-3 rounded-xl border border-neutral-300 focus:ring-2 focus:ring-lavender-300 outline-none"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>

          {/* Message */}
          <div>
            <label className="block text-neutral-700 font-semibold mb-2">
              Message
            </label>
            <textarea
              className="w-full p-3 h-32 rounded-xl border border-neutral-300 focus:ring-2 focus:ring-lavender-300 outline-none resize-none"
              placeholder="Write your message..."
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              required
            ></textarea>
          </div>

          {/* Button */}
          <button
            type="submit"
            className="px-8 py-3 bg-peach-300 text-white font-semibold rounded-full shadow-soft-lg hover:opacity-90 transition w-fit"
          >
            Send Message
          </button>
        </form>
      </motion.div>
    </section>
  );
}
