import React from "react";
import { motion } from "framer-motion";
import beach from "../assets/beach.jpg";


export default function Hero() {
  return (
    <section
      id="home"
      className="min-h-screen flex flex-col md:flex-row items-center justify-center gap-16 px-8 pt-20 pb-20"
    >
      {/* LEFT SIDE — CIRCLE GRADIENT BLOCK */}
      <motion.div
        initial={{ opacity: 0, x: -30 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ duration: 0.6 }}
        className="flex-1 flex justify-center"
      >
        <div
          className="w-60 h-60 md:w-80 md:h-80 rounded-full shadow-soft-lg border border-white/50"
          style={{
            background: "linear-gradient(135deg, #E6D7F7, #FFCBA4)",
          }}
        ></div>
      </motion.div>

      {/* RIGHT SIDE — TEXT CONTENT */}
      <motion.div
        initial={{ opacity: 0, x: 30 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ duration: 0.6 }}
        className="flex-1 max-w-xl"
      >
        <p className="text-sm tracking-wider text-lavender-400 font-semibold">
          CSE STUDENT • MERN STACK DEVELOPER
        </p>

        <h1 className="mt-2 text-5xl md:text-6xl font-extrabold">
          I’M{" "}
          <span className="text-peach-400">
            GUDIPUDI DURGA SWATHI
          </span>
        </h1>

        <p className="mt-6 text-neutral-700 leading-relaxed text-base md:text-lg">
          I build elegant MERN stack applications with clean UI, smooth
          functionality, and beautiful pastel aesthetics. Passionate about
          development, design, and creating meaningful digital experiences.
        </p>

        {/* BUTTONS */}
        <div className="mt-8 flex gap-4 flex-wrap">
          <a
            href="#projects"
            className="px-6 py-3 rounded-full border border-peach-400 text-peach-400 hover:bg-peach-300 hover:text-white transition font-medium"
          >
            View Projects
          </a>

          <a
            href="#contact"
            className="px-6 py-3 rounded-full border border-lavender-400 text-lavender-400 hover:bg-lavender-400 hover:text-white transition font-medium"
          >
            Contact Me
          </a>
        </div>
      </motion.div>
    </section>
  );
}
