import React from "react";
import { motion } from "framer-motion";

export default function Skills() {
  const skills = [
    { name: "Python", level: "85%" },
    { name: "C Language", level: "70%" },
    { name: "Java", level: "75%" },
    { name: "HTML & CSS", level: "90%" },
    { name: "MERN Stack", level: "80%" },
    { name: "MongoDB", level: "75%" },
    { name: "SQL / DBMS", level: "85%" },
    { name: "Microsoft Office", level: "95%" },
  ];

  return (
    <section id="skills" className="min-h-screen px-8 py-24">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        viewport={{ once: true }}
        className="max-w-4xl mx-auto"
      >
        {/* SECTION HEADING */}
        <h2 className="text-4xl font-extrabold text-lavender-400 text-center">
          Skills
        </h2>
        <div className="w-24 h-2 bg-peach-300 rounded-full mx-auto mt-2 mb-12"></div>

        {/* SKILL BARS */}
        <div className="space-y-8">
          {skills.map((skill, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, x: -40 }}
              whileInView={{ opacity: 1, x: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.6, delay: index * 0.1 }}
            >
              <div className="flex justify-between mb-2">
                <span className="text-neutral-800 font-semibold">{skill.name}</span>
                <span className="text-peach-400 font-semibold">{skill.level}</span>
              </div>

              <div className="w-full h-3 bg-lavender-200 rounded-full overflow-hidden">
                <motion.div
                  initial={{ width: 0 }}
                  whileInView={{ width: skill.level }}
                  transition={{ duration: 1.2 }}
                  className="h-3 rounded-full"
                  style={{ backgroundColor: "#C8A2C8" }} // lavender progress bar
                ></motion.div>
              </div>
            </motion.div>
          ))}
        </div>
      </motion.div>
    </section>
  );
}
