import React from 'react';

export default function Hero() {
  return (
    <section className="my-8">
      <div className="card flex flex-col md:flex-row items-center gap-6">
        <div className="flex-1">
          <h1 className="text-3xl md:text-4xl font-bold">Gudipudi Durga Swathi</h1>
          <p className="mt-2 text-lg">Final-year CSE Student | MERN Stack Developer | AI Enthusiast</p>
          <p className="mt-4">I build responsive web apps and intelligent tools — focused on clean UI, scalable backends, and meaningful user experiences.</p>

          <div className="mt-4 flex gap-3">
            <a href="/resume.pdf" target="_blank" rel="noreferrer" className="px-4 py-2 rounded-full border">Download Resume</a>
            <a href="#projects" className="px-4 py-2 rounded-full bg-black text-white">See Projects</a>
          </div>
        </div>
        <div className="w-40 h-40 bg-gradient-to-tr from-indigo-400 to-pink-400 rounded-2xl flex items-center justify-center text-white font-semibold">CSE • Final Year</div>
      </div>
    </section>
  );
}
